def get_min_token_transfer_threshold(symbol):
    return config['TOKENS'][config['CURRENT_OP_NETWORK']][symbol].get('min_transfer_threshold', config['MIN_TOKEN_TRANSFER_THRESHOLD'])


def get_contract_address(symbol):
    return config["TOKENS"][config["CURRENT_OP_NETWORK"]][symbol]["contract_address"]
    

def get_contract_abi(symbol):
    return config["TOKENS"][config["CURRENT_OP_NETWORK"]][symbol]["abi"]
